<?php
/**
 * Test class for Mergy_Util_Parallel.
 * Generated by PHPUnit on 2011-10-23 at 17:48:39.
 */
class Mergy_Util_ParallelTest extends PHPUnit_Framework_TestCase {

    /**
     * The object to test with
     *
     * @var string
     */
    const TEST_MOCK = 'Exception';

    /**
     * @var Mergy_Util_Parallel
     */
    protected $object;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp() {
        $aStack = array(
            $this->getMock(self::TEST_MOCK),
            $this->getMock(self::TEST_MOCK)
        );

        $this->object = new Mergy_Util_Parallel($aStack);
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown() {
    }

    /**
     * Set the number of threads
     */
    public function testThreads() {
        $this->assertInstanceOf('Mergy_Util_Parallel', $this->object->threads(2));
    }

    /**
     * Test running methods in parallel
     *
     * @depends testThreads
     */
    public function testRun() {
        $this->assertInstanceOf('Mergy_Util_Parallel', $this->object->run(array(
            'getMessage'
        )));
    }

    /**
     * Test getting the result
     *
     * @depends testRun
     */
    public function testGet() {
        $aStack = $this->object->get();
        $this->assertInternalType('array', $aStack);
        $this->assertEquals(2, count($aStack));
        foreach ($aStack as $oProject) {
            $this->assertInstanceOf(self::TEST_MOCK, $oProject);
        }
    }
}
